begin
wwv_flow_api.create_plugin(
 p_id=>wwv_flow_api.id(24147135284355378528)
,p_plugin_type=>'DYNAMIC ACTION'
,p_name=>'RW.APEX.CLIENT.TIMER'
,p_display_name=>'APEX Client Side Timer'
,p_category=>'EXECUTE'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'FUNCTION F_RENDER (',
'    P_DYNAMIC_ACTION   IN   APEX_PLUGIN.T_DYNAMIC_ACTION,',
'    P_PLUGIN           IN   APEX_PLUGIN.T_PLUGIN',
') RETURN APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT AS',
'',
'    L_RESULT        APEX_PLUGIN.T_DYNAMIC_ACTION_RENDER_RESULT;',
'    VR_TYPE         CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_01%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_01;',
'    VR_STATIC_VALUE CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_02%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_02;',
'    VR_ITEM_NAME    CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_03%TYPE := P_DYNAMIC_ACTION.ATTRIBUTE_03;',
'    VR_ITEM_ID      CONSTANT P_DYNAMIC_ACTION.ATTRIBUTE_03%TYPE := APEX_PLUGIN_UTIL.PAGE_ITEM_NAMES_TO_JQUERY(VR_ITEM_NAME);',
'BEGIN',
'',
'    APEX_JAVASCRIPT.ADD_LIBRARY(',
'        P_NAME                   => ''script'',',
'        P_CHECK_TO_ADD_MINIFIED   => TRUE,',
'        P_DIRECTORY              => P_PLUGIN.FILE_PREFIX,',
'        P_KEY                    => ''acstjssrc''',
'    );',
'',
'    L_RESULT.JAVASCRIPT_FUNCTION := ''function () { apexClientSideTimer.initialize(this,'' ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_TYPE,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_STATIC_VALUE,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_ITEM_NAME,TRUE) ||',
'        APEX_JAVASCRIPT.ADD_VALUE(VR_ITEM_ID,FALSE) ||',
'    ''); }'';',
'',
'    RETURN L_RESULT;',
'END;'))
,p_api_version=>1
,p_render_function=>'F_RENDER'
,p_standard_attributes=>'ITEM:BUTTON:REGION:JQUERY_SELECTOR:REQUIRED'
,p_substitute_attributes=>true
,p_subscribe_plugin_settings=>true
,p_help_text=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<p>This plug-in is used to trigger a Dynamic Action cyclic, e.g. to reload a region every 10 seconds. The timer can be set statically or per item value. Using the item value feature the timer can be stopped or reset by changing the item value.</p>',
'<p><b>IMPORTANT: The timer fires an event on the selected Affected Element so it is advisable to use only one trigger per element, but you have enough elements on the page and it doesn''t matter which one you use, e.g. the region that is updated or a '
||'page item</b></p>'))
,p_version_identifier=>'1.0'
,p_about_url=>'https://github.com/RonnyWeiss/APEX-Client-Side-Timer'
,p_files_version=>1359
);
